import React from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';

// Import the image using the correct relative path
import ui2designImage from '../images/ui2design.png';
import sparkImage from '../images/spark.png';

const HomePage = () => {
  return (
    <Box
      sx={{
        position: 'relative',
        minHeight: '85vh',
        overflow: 'hidden',
        display: 'flex',
        justifyContent: 'flex-start', // Align content to the left
        alignItems: 'center',
        background: 'linear-gradient(to bottom, #304352, #d7d2cc)',
      }}
    >
      {/* Image on the top right corner */}
      <img
        src={sparkImage}
        alt='Top Right Image'
        style={{
          width: '5%',
          top: '1%',
          height: 'auto',
          borderRadius: '8px',
          zIndex: 2,
        }}
      />
      {/* First square box with 3D effect */}
      <Box
        sx={{
          width: '45%',
          height: '60vh',
          borderRadius: '16px',
          left: '6%',
          overflow: 'hidden',
          background: 'linear-gradient(to bottom, #304352, #d7d2cc)',
          boxShadow: '10px 10px 20px rgba(0, 0, 0, 0.2)',
          position: 'relative',
          zIndex: 1,
        }}
      >
        {/* Content inside the first card */}
        <Box
          sx={{
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'space-between',
            alignItems: 'center',
            padding: '35px',
            color: 'white',
          }}
        >
          <Box>
            <Typography variant='h3' fontWeight={700} fontFamily='Roboto'>
              Elevate Your Designs with Design Boost
            </Typography>
            <Typography variant='subtitle1' mt={2}>
              Unleash the power of stunning and user-friendly interfaces.
            </Typography>
          </Box>
          {/* Image on the top right corner */}
          <img
            src={ui2designImage}
            alt='Top Right Image'
            style={{
              width: '80%',
              height: 'auto',
              borderRadius: '8px',
              zIndex: 2,
            }}
          />
        </Box>
      </Box>

      {/* Second square box for the 3D effect */}
      <Box
        sx={{
          width: '42%',
          height: '60vh',
          borderRadius: '16px',
          overflow: 'hidden',
          background: 'linear-gradient(to bottom, #304352, #d7d2cc)',
          boxShadow: '10px 10px 20px rgba(0, 0, 0, 0.2)',
          position: 'absolute',
          top: '17%',
          left: '4%',
          zIndex: 0,
        }}
      />
    </Box>
  );
};

export default HomePage;
