// HomePage.js
import React from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';

const HomePage = () => {
  return (
    <Box sx={{ position: 'relative', minHeight: '100vh', overflow: 'hidden' }}>
      {/* Card container */}
      <Box
        sx={{
          position: 'absolute',
          top: '50%',
          left: '5%',
          transform: 'translateY(-50%)',
          textAlign: 'left',
          width: '90%',
          display: 'flex',
          boxShadow: '10px 10px 10px rgba(0, 0, 0, 0.2)', // 3D effect using box-shadow
        }}
      >
        {/* Text on the left */}
        <Box sx={{ flex: '1', padding: '20px', backgroundColor: 'white' }}>
          <Typography variant='h3' fontWeight={700} fontFamily='monospace'>
            Elevate Your Designs with Design Boost
          </Typography>
          <Typography variant='subtitle1' mt={2}>
            Unleash the power of stunning and user-friendly interfaces.
          </Typography>
        </Box>

        {/* Image on the right */}
        <Box
          sx={{
            flex: '1',
            backgroundImage: 'url("path/to/your/image.jpg")',
            backgroundSize: 'cover',
          }}
        />
      </Box>
    </Box>
  );
};

export default HomePage;
