// HomePage.js
import React from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';

const HomePage = () => {
  return (
    <Box sx={{ position: 'relative', minHeight: '100vh', overflow: 'hidden' }}>
      {/* 3D square with gradient color */}
      <Box
        sx={{
          position: 'absolute',
          top: '50%',
          left: '5%',
          transform: 'translateY(-50%)',
          textAlign: 'left',
          width: '90%',
          display: 'flex',
        }}
      >
        {/* Gradient square with 3D effect */}
        <Box
          sx={{
            flex: '1',
            padding: '20px',
            background: 'linear-gradient(to right, #311b92, #6200ea)',
            color: 'white',
            boxShadow: '10px 10px 10px rgba(0, 0, 0, 0.2)', // 3D effect using box-shadow
          }}
        >
          <Typography variant='h3' fontWeight={700} fontFamily='monospace'>
            Elevate Your Designs with Design Boost
          </Typography>
          <Typography variant='subtitle1' mt={2}>
            Unleash the power of stunning and user-friendly interfaces.
          </Typography>
        </Box>

        {/* Randomly placed sparkling dots */}
        <Box
          sx={{
            flex: '1',
            position: 'relative',
            backgroundImage: 'url("path/to/your/image.jpg")',
            backgroundSize: 'cover',
            overflow: 'hidden',
          }}
        >
          {[...Array(10)].map((_, index) => (
            <Box
              key={index}
              sx={{
                position: 'absolute',
                width: '10px',
                height: '10px',
                borderRadius: '50%',
                background: 'white',
                opacity: 0.8,
                top: `${Math.random() * 100}%`,
                left: `${Math.random() * 100}%`,
                transform: 'translate(-50%, -50%)',
              }}
            />
          ))}
        </Box>
      </Box>
    </Box>
  );
};

export default HomePage;
