import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import Menu from '@mui/material/Menu';
import MenuIcon from '@mui/icons-material/Menu';
import Container from '@mui/material/Container';
import Button from '@mui/material/Button';
import { Link } from 'react-router-dom';

const pages = ['Home', 'Upload & Analysis', 'Feedback'];

function Appbar() {
  const [anchorElNav, setAnchorElNav] = React.useState(null);

  const handleOpenNavMenu = (event) => {
    setAnchorElNav(event.currentTarget);
  };

  const handleCloseNavMenu = () => {
    setAnchorElNav(null);
  };

  const navigateTo = (path) => {
    handleCloseNavMenu();
    window.location.href = path;
  };

  return (
    <AppBar
      position='static'
      style={{ background: 'linear-gradient(to bottom, #304352, #d7d2cc)' }}
    >
      <Container maxWidth='xl'>
        <Toolbar disableGutters>
          <Box sx={{ flexGrow: 1, display: 'flex', alignItems: 'center' }}>
            <IconButton
              size='large'
              aria-label='menu'
              aria-controls='menu-appbar'
              aria-haspopup='true'
              onClick={handleOpenNavMenu}
              color='inherit'
              sx={{ display: { xs: 'block', md: 'none' } }}
            >
              <MenuIcon />
            </IconButton>
            <Typography
              variant='h6'
              noWrap
              component={Link}
              to='/'
              sx={{
                mx: 'auto',
                fontFamily: 'monospace',
                fontWeight: 700,
                letterSpacing: '.3rem',
                color: 'white',
                textDecoration: 'none',
              }}
            >
              Design Boost
            </Typography>
          </Box>

          <Box sx={{ display: { xs: 'none', md: 'flex' } }}>
            {pages.map((page) => (
              <Button
                key={page}
                component={Link}
                to={`/${page.toLowerCase().replace(' ', '-')}`}
                onClick={handleCloseNavMenu}
                sx={{ mx: 2, color: 'white' }}
              >
                {page}
              </Button>
            ))}
            <Button
              onClick={() => navigateTo('/signin')}
              sx={{ mx: 2, color: 'white' }}
            >
              Sign In
            </Button>
            <Button
              onClick={() => navigateTo('/signup')}
              variant='contained'
              sx={{ mx: 2, color: 'white', backgroundColor: '#304352' }}
            >
              Sign Up
            </Button>
          </Box>

          <Menu
            id='menu-appbar'
            anchorEl={anchorElNav}
            anchorOrigin={{
              vertical: 'top',
              horizontal: 'left',
            }}
            keepMounted
            transformOrigin={{
              vertical: 'top',
              horizontal: 'left',
            }}
            open={Boolean(anchorElNav)}
            onClose={handleCloseNavMenu}
            sx={{
              display: { xs: 'block', md: 'none' },
            }}
          >
            {pages.map((page) => (
              <Button
                key={page}
                component={Link}
                to={`/${page.toLowerCase().replace(' ', '-')}`}
                onClick={handleCloseNavMenu}
                sx={{ color: 'black', width: '100%', textAlign: 'left' }}
              >
                {page}
              </Button>
            ))}
            <Button
              href='/signin'
              target='_blank' // Add this line for opening in a new tab/window
              onClick={handleCloseNavMenu}
              sx={{ mx: 2, color: 'white' }}
            >
              Sign In
            </Button>
            <Button
              href='/signup'
              target='_blank' // Add this line for opening in a new tab/window
              onClick={handleCloseNavMenu}
              variant='contained'
              sx={{ mx: 2, color: 'white', backgroundColor: '#304352' }}
            >
              Sign Up
            </Button>
          </Menu>
        </Toolbar>
      </Container>
    </AppBar>
  );
}

export default Appbar;
