import React from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import IconButton from '@mui/material/IconButton';
import ServiceIcon1 from '@mui/icons-material/AccessibilityNew';
import ServiceIcon2 from '@mui/icons-material/Extension';
import ServiceIcon3 from '@mui/icons-material/Settings';

const Services = () => {
  return (
    <Box
      sx={{
        width: '185vh',
        padding: '50px',
        height: '45vh',
        background: 'linear-gradient(to bottom, #304352, #d7d2cc)',
        color: 'white',
      }}
    >
      <Typography variant='h4' fontWeight={700} fontFamily='Roboto'>
        Our Services
      </Typography>

      {/* Cards layout for services */}
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'space-between',
          marginTop: '30px',
        }}
      >
        {/* Service Card 1 */}
        <Card
          sx={{
            width: '30%',
            borderRadius: '16px',
            height: '30vh',
            backgroundColor: 'rgba(255, 255, 255, 0.9)',
            boxShadow: '10px 10px 20px rgba(0, 0, 0, 0.2)',
            transition: 'transform 0.3s ease-in-out', // CSS transition for the transform property
            '&:hover': {
              transform: 'translateY(-10px)', // Move the card upward on hover
            },
          }}
        >
          <CardContent>
            <IconButton size='large'>
              <ServiceIcon1 fontSize='inherit' />
            </IconButton>
            <Typography variant='h6' fontWeight={700} mb={2}>
              Service 1
            </Typography>
            <Typography variant='body1'>
              Description of Service 1. Provide details about what this service
              offers.
            </Typography>
          </CardContent>
        </Card>

        {/* Service Card 2 */}
        <Card
          sx={{
            width: '30%',
            borderRadius: '16px',
            backgroundColor: 'rgba(255, 255, 255, 0.9)',
            boxShadow: '10px 10px 20px rgba(0, 0, 0, 0.2)',
            transition: 'transform 0.3s ease-in-out',
            '&:hover': {
              transform: 'translateY(-10px)',
            },
          }}
        >
          <CardContent>
            <IconButton size='large'>
              <ServiceIcon2 fontSize='inherit' />
            </IconButton>
            <Typography variant='h6' fontWeight={700} mb={2}>
              Service 2
            </Typography>
            <Typography variant='body1'>
              Description of Service 2. Provide details about what this service
              offers.
            </Typography>
          </CardContent>
        </Card>

        {/* Service Card 3 */}
        <Card
          sx={{
            width: '30%',
            borderRadius: '16px',
            backgroundColor: 'rgba(255, 255, 255, 0.9)',
            boxShadow: '10px 10px 20px rgba(0, 0, 0, 0.2)',
            transition: 'transform 0.3s ease-in-out',
            '&:hover': {
              transform: 'translateY(-10px)',
            },
          }}
        >
          <CardContent>
            <IconButton size='large'>
              <ServiceIcon3 fontSize='inherit' />
            </IconButton>
            <Typography variant='h6' fontWeight={700} mb={2}>
              Service 3
            </Typography>
            <Typography variant='body1'>
              Description of Service 3. Provide details about what this service
              offers.
            </Typography>
          </CardContent>
        </Card>
      </Box>
    </Box>
  );
};

export default Services;
