// HomePage.js
import React from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';

const HomePage = () => {
  return (
    <Box sx={{ position: 'relative', minHeight: '100vh', overflow: 'hidden' }}>
      {/* Card container */}
      <Box
        sx={{
          position: 'absolute',
          top: '50%',
          left: '5%',
          transform: 'translateY(-50%)',
          textAlign: 'left',
          width: '90%',
          display: 'flex',
          boxShadow: '10px 10px 10px rgba(0, 0, 0, 0.2)', // 3D effect using box-shadow
          background: 'linear-gradient(to right, #311b92, #000000)', // Gradient of black and purple
        }}
      >
        {/* Text on the left */}
        <Box
          sx={{
            flex: '1',
            padding: '20px',
            backgroundColor: 'black',
            color: 'white',
          }}
        >
          <Typography variant='h3' fontWeight={700} fontFamily='monospace'>
            Elevate Your Designs with Design Boost
          </Typography>
          <Typography variant='subtitle1' mt={2}>
            Unleash the power of stunning and user-friendly interfaces.
          </Typography>
        </Box>

        {/* Image on the right */}
        <Box
          sx={{
            flex: '1',
            backgroundImage: 'url("path/to/your/image.jpg")',
            backgroundSize: 'cover',
            position: 'relative',
          }}
        >
          {/* Sparkling dots */}
          <Box
            sx={{
              position: 'absolute',
              top: '0',
              right: '0',
              width: '50px',
              height: '50px',
              background: 'transparent',
              transform: 'rotate(45deg)',
            }}
          >
            <Box
              sx={{
                position: 'absolute',
                width: '10px',
                height: '10px',
                borderRadius: '50%',
                background: 'white',
                opacity: 0.8,
                transform: 'translate(15px, -15px)',
              }}
            />
            <Box
              sx={{
                position: 'absolute',
                width: '10px',
                height: '10px',
                borderRadius: '50%',
                background: 'white',
                opacity: 0.8,
                transform: 'translate(30px, -30px)',
              }}
            />
            {/* Add more dots as needed */}
          </Box>
        </Box>
      </Box>
    </Box>
  );
};

export default HomePage;
