// HomePage.js
import React from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';

const HomePage = () => {
  return (
    <Box sx={{ position: 'relative', minHeight: '100vh', overflow: 'hidden' }}>
      {/* Card-like square with gradient color and 3D effect */}
      <Box
        sx={{
          position: 'absolute',
          top: '50%',
          left: '2%',
          transform: 'translateY(-50%)',
          textAlign: 'left',
          width: '96%',
          height: '80%',
          display: 'flex',
          borderRadius: '16px', // Rounded corners for a card-like appearance
          overflow: 'hidden',
        }}
      >
        {/* Gradient square with 3D effect */}
        <Box
          sx={{
            flex: '1',
            padding: '40px',
            color: 'white',
            background: 'linear-gradient(to right, #311b92, #6200ea)', // Gradient of black and purple
            boxShadow: '10px 10px 20px rgba(0, 0, 0, 0.2)', // 3D effect using box-shadow
          }}
        >
          <Typography variant='h3' fontWeight={700} fontFamily='monospace'>
            Elevate Your Designs with Design Boost
          </Typography>
          <Typography variant='subtitle1' mt={2}>
            Unleash the power of stunning and user-friendly interfaces.
          </Typography>
        </Box>
      </Box>

      {/* Purple layer underneath for a 3D effect */}
      <Box
        sx={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          width: '100%',
          height: '80%',
          background: '#6200ea', // Purple color for the 3D effect
        }}
      />
    </Box>
  );
};

export default HomePage;
