import React from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';

// Import the image using the correct relative path
import ui2designImage from '../images/ui2design.png';
import happyImage from '../images/happy.png';
import sadcustomerImage from '../images/sadcutomer.png';
import lefthandImage from '../images/lefthand.png';
import righthandImage from '../images/righthand.png';

const HomePage = () => {
  return (
    <Box
      sx={{
        position: 'relative',
        minHeight: '100vh',
        overflow: 'hidden',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center', // Align content to the center vertically
        background: 'linear-gradient(to bottom, #304352, #d7d2cc)', // Set the background color for the 3D effect layer
      }}
    >
      <img
        src={lefthandImage} // Use the imported variable
        alt='Image'
        style={{
          position: 'absolute',
          top: '200px',
          left: '-10px',
          width: '360px', // Adjust the width as needed
          height: '300px',
          borderRadius: '8px',
          zIndex: 2, // Ensure the image is above the background layer
        }}
      />

      {/* Second square box for the 3D effect */}
      <Box
        sx={{
          width: '60%',
          height: '60vh',
          borderRadius: '16px', // Rounded corners for a card-like appearance
          overflow: 'hidden',
          background: 'linear-gradient(to bottom, #304352, #d7d2cc)', // Gradient of dark purple to light purple
          boxShadow: '10px 10px 20px rgba(0, 0, 0, 0.2)', // 3D effect using box-shadow
          position: 'absolute', // Absolute positioning for stacking behind the first box
          top: '17%',
          left: '18%',
          zIndex: 0, // Ensure the box is behind the first one
        }}
      />

      {/* First square box with 3D effect */}
      <Box
        sx={{
          width: '60%',
          height: '60vh',
          borderRadius: '16px', // Rounded corners for a card-like appearance
          overflow: 'hidden',
          background: 'linear-gradient(to bottom, #304352, #d7d2cc)', // Gradient of dark purple to light purple
          boxShadow: '10px 10px 20px rgba(0, 0, 0, 0.2)', // 3D effect using box-shadow
          position: 'relative', // Ensure relative positioning for z-index to work
          zIndex: 1, // Ensure the card is above the background layer
        }}
      >
        {/* Content inside the first card */}
        <Box
          sx={{
            display: 'flex',
            justifyContent: 'space-between', // Distribute content
            alignItems: 'center', // Align content vertically
            padding: '35px',
            color: 'white',
          }}
        >
          <Box sx={{ flexGrow: 1 }}>
            <Typography variant='h3' fontWeight={700} fontFamily='Roboto'>
              Elevate Your Designs with Design Boost
            </Typography>
            <Typography variant='subtitle1' mt={2}>
              Unleash the power of stunning and user-friendly interfaces.
            </Typography>
            {/* Image on the top right corner */}
            <img
              src={ui2designImage} // Use the imported variable
              alt='Top Right Image'
              style={{
                position: 'absolute',
                top: '150px',
                left: '496px',
                width: '500px', // Adjust the width as needed
                height: '300px',
                borderRadius: '8px',
                zIndex: 2, // Ensure the image is above the background layer
              }}
            />
          </Box>
        </Box>
      </Box>
    </Box>
  );
};

export default HomePage;
