import React from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';

const HomePage = () => {
  return (
    <Box
      sx={{
        width: '60%',
        height: '60vh',
        marginTop: '10%',
        marginLeft: '30%',
        marginRight: '15%',
        borderRadius: '16px', // Rounded corners for a card-like appearance
        overflow: 'hidden',
        background: 'linear-gradient(to right, #311b92, #6200ea)', // Gradient of black and purple
        boxShadow: '10px 10px 20px rgba(0, 0, 0, 0.2)', // 3D effect using box-shadow
        position: 'relative', // Ensure relative positioning for z-index to work
        zIndex: 1, // Ensure the card is above the background layer
      }}
    >
      {/* Content inside the card */}
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'space-between', // Distribute content
          alignItems: 'center', // Align content vertically
          padding: '40px',
          color: 'white',
        }}
      >
        <Box sx={{ flexGrow: 1 }}>
          <Typography variant='h3' fontWeight={700} fontFamily='Roboto'>
            Elevate Your Designs with Design Boost
          </Typography>
          <Typography variant='subtitle1' mt={2}>
            Unleash the power of stunning and user-friendly interfaces.
          </Typography>
        </Box>
      </Box>
    </Box>
  );
};

export default HomePage;
