import React from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';

// Import the image using the correct relative path
import ui2designImage from '../images/ui2design.png';

const HomePage = () => {
  return (
    <Box
      sx={{
        position: 'relative',
        minHeight: '100vh',
        overflow: 'hidden',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center', // Align content to the center vertically
        background: 'linear-gradient(to right, #3f007d, #000000)', // Set the background color for the 3D effect layer
      }}
    >
      {/* Second square box for the 3D effect */}
      <Box
        sx={{
          width: '60%',
          height: '60vh',
          borderRadius: '16px', // Rounded corners for a card-like appearance
          overflow: 'hidden',
          background: 'linear-gradient(to right, #3f007d, #000000)', // Gradient of dark purple to black
          boxShadow: '10px 10px 20px rgba(0, 0, 0, 0.2)', // 3D effect using box-shadow
          position: 'absolute', // Absolute positioning for stacking behind the first box
          top: '17%',
          left: '18%',
          zIndex: 0, // Ensure the box is behind the first one
        }}
      />

      {/* Image on the top right corner */}
      <img
        src={ui2designImage} // Use the imported variable
        alt='Top Right Image'
        style={{
          position: 'absolute',
          top: '10px',
          right: '10px',
          width: '50px', // Adjust the width as needed
          height: 'auto',
          borderRadius: '8px',
          zIndex: 2, // Ensure the image is above the background layer
        }}
      />

      {/* First square box with 3D effect */}
      <Box
        sx={{
          width: '60%',
          height: '60vh',
          borderRadius: '16px', // Rounded corners for a card-like appearance
          overflow: 'hidden',
          background: 'linear-gradient(to right, #311b92, #6200ea)', // Gradient of black and purple
          boxShadow: '10px 10px 20px rgba(0, 0, 0, 0.2)', // 3D effect using box-shadow
          position: 'relative', // Ensure relative positioning for z-index to work
          zIndex: 1, // Ensure the card is above the background layer
        }}
      >
        {/* Content inside the first card */}
        <Box
          sx={{
            display: 'flex',
            justifyContent: 'space-between', // Distribute content
            alignItems: 'center', // Align content vertically
            padding: '40px',
            color: 'white',
          }}
        >
          <Box sx={{ flexGrow: 1 }}>
            <Typography variant='h3' fontWeight={700} fontFamily='Roboto'>
              Elevate Your Designs with Design Boost
            </Typography>
            <Typography variant='subtitle1' mt={2}>
              Unleash the power of stunning and user-friendly interfaces.
            </Typography>
          </Box>
        </Box>
      </Box>
    </Box>
  );
};

export default HomePage;
