import React from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';

// Import the image using the correct relative path
import ui2designImage from '../images/ui2design.png';
import sparkImage from '../images/spark.png';
import arrowImage from '../images/arrow.png';
import boostImage from '../images/boost.png';
import Services from './services';
const HomePage = () => {
  return (
    <Box>
      <Box
        sx={{
          position: 'relative',
          minHeight: '85vh',
          overflow: 'hidden',
          display: 'flex',
          justifyContent: 'flex-start', // Align content to the left
          alignItems: 'center',
          background: 'linear-gradient(to bottom, #304352, #d7d2cc)',
        }}
      >
        {/* Spark image on the top left corner */}
        <img
          src={sparkImage}
          alt='Spark Image'
          style={{
            width: '9%',
            top: '3%',
            left: '1%',
            height: 'auto',
            borderRadius: '8px',
            zIndex: 2,
            position: 'absolute',
          }}
        />

        <img
          src={arrowImage}
          alt='arrow Image'
          style={{
            width: '23%',
            top: '48%',
            left: '51%',
            height: 'auto',
            borderRadius: '8px',
            zIndex: 2,
            position: 'absolute',
          }}
        />

        <img
          src={boostImage}
          alt='boost Image'
          style={{
            width: '25%',
            top: '10%',
            left: '70%',
            height: 'auto',
            borderRadius: '8px',
            zIndex: 2,
            position: 'absolute',
          }}
        />

        {/* First square box with 3D effect */}
        <Box
          sx={{
            width: '45%',
            height: '60vh',
            borderRadius: '16px',
            left: '6%',
            overflow: 'hidden',
            background: 'linear-gradient(to bottom, #304352, #d7d2cc)',
            boxShadow: '10px 10px 20px rgba(0, 0, 0, 0.2)',
            position: 'relative',
            zIndex: 1,
          }}
        >
          {/* Content inside the first card */}
          <Box
            sx={{
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'space-between',
              alignItems: 'center',
              padding: '35px',
              color: 'white',
            }}
          >
            <Box>
              <Typography variant='h3' fontWeight={700} fontFamily='Roboto'>
                Elevate Your Designs with Design Boost
              </Typography>
              <Typography variant='subtitle1' mt={2}>
                Unleash the power of stunning and user-friendly interfaces.
              </Typography>
            </Box>
            {/* Image on the top right corner */}
            <img
              src={ui2designImage}
              alt='Top Right Image'
              style={{
                width: '80%',
                height: 'auto',
                borderRadius: '8px',
                zIndex: 2,
              }}
            />
          </Box>
        </Box>

        {/* Second square box for the 3D effect */}
        <Box
          sx={{
            width: '42%',
            height: '60vh',
            borderRadius: '16px',
            overflow: 'hidden',
            background: 'linear-gradient(to bottom, #304352, #d7d2cc)',
            boxShadow: '10px 10px 20px rgba(0, 0, 0, 0.2)',
            position: 'absolute',
            top: '17%',
            left: '4%',
            zIndex: 0,
          }}
        />
      </Box>
    </Box>
  );
};

export default HomePage;
