import React from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';

// Import the image using the correct relative path
import ui2designImage from '../images/ui2design.png';
import happyImage from '../images/happy.png';
import sadcustomerImage from '../images/sadcutomer.png';
import lefthandupdateImage from '../images/lefthandupdate.png';
import righthandupdateImage from '../images/righthandupdate.png';

const HomePage = () => {
  return (
    <Box
      sx={{
        position: 'relative',
        minHeight: '100vh',
        overflow: 'hidden',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        background: 'linear-gradient(to bottom, #304352, #d7d2cc)',
      }}
    >
      {/* Image components with responsive styling */}
      <img
        src={lefthandupdateImage}
        alt='Image'
        style={{
          position: 'absolute',
          top: ['10%', '118px'],
          left: ['2%', '15px'],
          width: '100%',
          height: 'auto',
          borderRadius: '0px',
          zIndex: 2,
        }}
      />

      <img
        src={sadcustomerImage}
        alt='Image'
        style={{
          position: 'absolute',
          top: ['45%', '430px'],
          left: ['2%', '15px'],
          width: '100%',
          height: 'auto',
          borderRadius: '0px',
          zIndex: 2,
        }}
      />

      <img
        src={happyImage}
        alt='Image'
        style={{
          position: 'absolute',
          top: ['45%', '430px'],
          right: ['2%', '10px'],
          width: '100%',
          height: 'auto',
          borderRadius: '0px',
          zIndex: 2,
        }}
      />

      <img
        src={righthandupdateImage}
        alt='Image'
        style={{
          position: 'absolute',
          top: ['10%', '142px'],
          right: ['2%', '45px'],
          width: '100%',
          height: 'auto',
          borderRadius: '0px',
          zIndex: 2,
        }}
      />

      {/* Second square box for the 3D effect */}
      <Box
        sx={{
          width: ['80%', '60%'],
          height: ['80vh', '60vh'],
          borderRadius: '16px',
          overflow: 'hidden',
          background: 'linear-gradient(to bottom, #304352, #d7d2cc)',
          boxShadow: '10px 10px 20px rgba(0, 0, 0, 0.2)',
          position: 'absolute',
          top: ['15%', '17%'],
          left: ['10%', '18%'],
          zIndex: 0,
        }}
      />

      {/* First square box with 3D effect */}
      <Box
        sx={{
          width: ['80%', '60%'],
          height: ['80vh', '60vh'],
          borderRadius: '16px',
          overflow: 'hidden',
          background: 'linear-gradient(to bottom, #304352, #d7d2cc)',
          boxShadow: '10px 10px 20px rgba(0, 0, 0, 0.2)',
          position: 'relative',
          zIndex: 1,
        }}
      >
        {/* Content inside the first card */}
        <Box
          sx={{
            display: 'flex',
            flexDirection: ['column', 'row'],
            justifyContent: 'space-between',
            alignItems: ['center', 'flex-start'],
            padding: ['20px', '35px'],
            color: 'white',
          }}
        >
          <Box sx={{ flexGrow: 1 }}>
            <Typography variant='h3' fontWeight={700} fontFamily='Roboto'>
              Elevate Your Designs with Design Boost
            </Typography>
            <Typography variant='subtitle1' mt={2}>
              Unleash the power of stunning and user-friendly interfaces.
            </Typography>
            {/* Image on the top right corner */}
            <img
              src={ui2designImage}
              alt='Top Right Image'
              style={{
                position: 'absolute',
                top: ['120px', '150px'],
                left: ['2%', '496px'],
                width: ['80%', '500px'],
                height: 'auto',
                borderRadius: '8px',
                zIndex: 2,
              }}
            />
          </Box>
        </Box>
      </Box>
    </Box>
  );
};

export default HomePage;
